import './App.css';
import {useState,useEffect} from 'react';
import axios from 'axios';

function App() {

  const [search, setsearch]=useState('')
const [allData, setAllData] = useState({
  city:'',
  country:'',
  temprature:'',
  humidity :'',
  minTempreature:'',
  weathericon :''
})

useEffect(()=> {

  fetchData()

},[])
const fetchData = async(city)=> {
  try{
  const APIKEY='bb8febd78fc11232c13cfbd4f68b8610'
  //allow us to make request to backend with promish
  const result = await axios.get(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${APIKEY}&units=metric`)
  await setAllData ({
    city:result.data.name,
    country: result.data.sys.country,
    temprature:result.data.main.temp,
    humidity: result.data.humidity,
    minTempreature: result.data.temp_min,
    weathericon: result.data.weather.icons
  })
  } catch (e){
    console.log('API not lodded correctly or lodded up for first time')
  }
} 

const handleChange= (event)=>{
  setsearch(event.target.value)
} 
const handleSubmit= (event)=>{
  //setsearch(event.target.value)
  console.log(search)
  event.preventDefault() 
  fetchData(search)
} 
  return (
<main>
<div className="App">
     <form onSubmit={handleSubmit}>
      <input
      value={search}
      type='text'
      name='city'
      placeholder='City Name'
      onChange={handleChange}
      />
      <button for ='city'>search</button>
     </form>
    <section>
      <h1>{allData.city}</h1>
      <h2> {allData.country}</h2>
      <div>
        <h3>
          temprature
        </h3>
      <p> {allData.temprature} Â°C</p>

      </div>
      <div>
        <h3>Min temprature</h3>
        <p>{allData.minTempreature}</p>
      </div>
    </section>

    </div>
</main>
  );
}

export default App;
